INSERT INTO `meituan_binhaileyuan` VALUES ('21', 'N7314', '602184192', 'https://img.meituan.net/avatar/809c6c19fe099420e3405a1a057ca17327161.jpg', '服务还不错，而且提供姜汤！提供姜汤！提供姜汤！\n然后，能见度很高，还可以在水里喂小鱼，鱼和龟什么的都很乖也不怕人，教练还给了小贝壳。\n而且活动项目很多(虽然因为个人原因只潜了水)，然后有储物柜和冲水的地方\n静等明天工作人员发照片(:3_ヽ)_', '2018-01-03 01:16:39', '', 'None');
