INSERT INTO `meituan_binhaileyuan` VALUES ('625', 'killy0492', '81410112', '', '服务一般吧，可能已经练就了问啥都当没听见的本领，环境可以，里面吃的很贵', '2016-08-16 15:44:29', '', 'None');
INSERT INTO `meituan_binhaileyuan` VALUES ('626', '彼翼航服', '234279204', '', '太坑，太差劲，先说景区里面就几只假恐龙，然后是沙滩项目要自费，贝壳馆以卖为主，滑冰体验15分钟，说是真冰里面还碧波荡漾的，最后说蝴蝶谷离景区老远了，景区不负责接送游客要自己去。', '2016-08-16 13:37:19', '', 'None');
INSERT INTO `meituan_binhaileyuan` VALUES ('627', 'CSh383799010', '578131118', '', '超值啊，就相当于一个公园，去走走看看挺不错的，亚龙湾风景相当美。', '2016-08-16 12:54:04', '', 'None');
