INSERT INTO `meituan_boao` VALUES ('776', 'GTz546788474', '250036016', 'https://img.meituan.net/avatar/c29044aea4973699ada41e8c4d227170178186.jpg', '之前作了功课，看了别人的评价，总觉得一人一个感受，个人觉得性价比还是满高的，团了2张43元的博鳌成立会址加玉带滩往返，出发的时候天公作美，下了毛毛细雨，觉得挺浪漫的，会址范围不大，适合拍照留念，之后坐小船去的玉带滩，非常兴奋，的确壮观，感觉非常良好，在沙滩上足足停留了3个小时，才恋恋不舍的返回，不虚此行，对于喜欢自然景观的我绝不后悔颇费周折来到博鳌，来到玉带滩，我是自由行，满刺激的，也喜欢的朋友来这里吧！', '2016-02-21 20:12:35', '-1936557695:http://p1.meituan.net/shaitu/241e3f0f9ee738e857fc59f8b50da0cd502023.jpg;\n-1936586445:http://p1.meituan.net/shaitu/f9073bca59aaa020fe796be3641c3be1676654.jpg;\n-1936410644:http://p0.meituan.net/shaitu/2835b5afb875bf3553e34abf04b59a2d680222.jpg;\n', 'None');
