INSERT INTO `meituan_dadonghai` VALUES ('252', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '教练很好，前台服务员也不错。但中间环节另几位态度有点生硬', '2014-04-10 02:13:55', '', 'None');
