INSERT INTO `meituan_dadonghai` VALUES ('293', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '教练真心不错，是个很负责的人，虽然潜的不深，总体还是很不错的。', '2014-02-06 16:25:02', '', 'None');
