INSERT INTO `meituan_dadonghai` VALUES ('295', 'Mr.kiwi.hu', '40026233', 'https://img.meituan.net/avatar/623c17e1d52a34c75149a5183a9c4979107056.jpg', '还是不错的拍照180五张，挺好的。就是潜到八米耳朵有点痛了，要休息休息了', '2014-01-27 22:23:15', '', 'None');
