INSERT INTO `meituan_daxiaodongtian` VALUES ('150', 'iWD387635969', '240427960', 'https://img.meituan.net/avatar/e0b0ce6f81fb761613970c419067747017474.jpg', '东临碣石，以观沧海\n水何澹澹，山岛竦峙\n\n树木丛生，百草丰茂\n秋风萧瑟，洪波涌起\n\n很幸福自己生活在三亚\n很幸福很欣赏到这样的美景\n\n乘坐25路车坐到终点就是大小洞天景点\n大小洞天是许多情侣选择拍婚纱照的人气景点之一\n里面有很多值得一看的地方\n可以乘坐里面的电瓶车\n也可以选择徒步行走\n\n自己徒步行走欣赏到的风景会更多一些\n不过建议老人孩子还是乘坐电瓶车\n15元一个人\n\n景点里还有可以攀登的山峰\n最上面的叫金顶\n可以欣赏到美丽海景\n山的垂直海拔还是比较高的\n这次我花了1个小时的时间\n才攀登到顶峰\n顶峰的景色的确很美\n\n但是金顶只是山的最高处\n可以看到更远的海\n\n下山的时候可以经过很多景点\n可以拍照留念\n建议大家如果想要仔细玩遍景区的景点\n需要拿出完整的一天或者半天来', '2018-08-21 23:46:23', '-1340760951:http://p1.meituan.net/shaitu/66cde00e4345d4a5cbf33c25bf26adbb497214.jpg;\n-1340856703:http://p0.meituan.net/shaitu/6dee1007efd97cee6ef9d323db7297c4644029.jpg;\n-1340760950:http://p1.meituan.net/shaitu/7aea8e2bc89993dcb5f13cdee8961379491057.jpg;\n-1340856702:http://p0.meituan.net/shaitu/0890aed7ac0db21190a5159fa3beb11b626488.jpg;\n-1340760949:http://p1.meituan.net/shaitu/25347223f828f4576253075e02f36fb9527328.jpg;\n-1340856701:http://p1.meituan.net/shaitu/d58a08971d4fa7c194678cefcec2abab676780.jpg;\n-1340760948:http://p0.meituan.net/shaitu/5a02a41357d7c83ff6b043a20b160855331306.jpg;\n', 'None');
