INSERT INTO `meituan_daxiaodongtian` VALUES ('649', '睡睡song', '233096083', 'https://img.meituan.net/avatar/f1538c5272cbd8d7cb35b7d647a6c79046922.jpg', '非常方便，当天预定就可以直接扫二维码进园，价格也比直接买门票优惠。景区风景很好，各种设施也很全面，非常干净，海水很清，山上有很多道教相关文化遗址，道教文化气氛浓厚，值得一去，对爬山感兴趣的人可以去试试金顶。', '2018-04-15 22:19:22', '-1462917309:http://p0.meituan.net/shaitu/a5f1a48678d7b7bdc614b28ff52b8e661421746.jpg;\n', 'None');
