INSERT INTO `meituan_daxiaodongtian` VALUES ('832', 'Ump30150910', '153248161', 'https://img.meituan.net/avatar/a7156be87d9d515f955bbcf05c568b7f34706.jpg', '3月18日二月二在美团买优惠价22元门票，与朋友4人坐25路公车去“大小洞天”景区，因为今天是二月初二龙抬头日子，景区很热闹，蓝天白云，海浪椰林；海面上帆船点点；秧歌、舞龙、庙会、小吃、冷饮；川流不息的游客，还有数十对拍婚纱的新人们，为他们献上美好的祝福。大小洞天景区不太大，不建议坐电瓶车，网上卖15元，有的景点在山上需要爬山，九年前爬过山很累，今天没去山顶。只是在“寿”石和“南山”石附近流连了很久，“南极仙翁”、“老子骑青牛\"、“南山不老松\"、“延寿桥”\n，环境真的很美！', '2018-03-18 20:09:45', '-1480462137:http://p0.meituan.net/shaitu/3197bf6fa6c4a0da7f676d1792c3c4fb270376.jpg;\n-1480365111:http://p0.meituan.net/shaitu/9f76d1fd4587d382ab3b5dee42739efb382092.jpg;\n-1480365114:http://p1.meituan.net/shaitu/5e7da9ff2d0cdfcb4fcd0f9731efeb20478898.jpg;\n-1480365112:http://p0.meituan.net/shaitu/9552afb6d7a3d2ff5c4e57e11494fc4b268954.jpg;\n-1480365113:http://p1.meituan.net/shaitu/12583df1c6307a7b5100e49694aa22ca507859.jpg;\n-1480365115:http://p1.meituan.net/shaitu/9b832f823424adc566126435d5a22f62413531.jpg;\n-1480462138:http://p1.meituan.net/shaitu/f5b4811d38318a6a519370cc284e47ef230236.jpg;\n-1480365116:http://p1.meituan.net/shaitu/616ba219dcc45e14bf29522a80ea872e267854.jpg;\n', 'None');
