INSERT INTO `meituan_daxiaodongtian` VALUES ('4810', '龙儿820', '26135931', 'https://img.meituan.net/avatar/__44591823__9583200.jpg', '挺好的不错，直接买二维码进就可以，不用换票，但讲解机万押金，一个100。可以坐电瓶车，节省体力，带宝宝的孩子的还可以在进门的时候要宝宝车，是免费的哦！我是后来才知道的。一路抱着宝宝还热……服务一般……啊', '2016-05-29 12:01:59', '-1941054485:http://p0.meituan.net/shaitu/88dd737b2f3d9b542639dc8e89e407cf381522.jpg;\n-1941034639:http://p0.meituan.net/shaitu/6119ad896090502a0e9b29f716e194c3243281.jpg;\n-1940880772:http://p1.meituan.net/shaitu/8576767cf2cbe9db814f14bb8c2b0c13496023.jpg;\n', 'None');
