INSERT INTO `meituan_daxiaodongtian` VALUES ('5060', '放杨的小玉', '276867979', 'https://img.meituan.net/avatar/20a9d254c95c9f18c87e96812cd34839132495.jpg', '风景很漂亮！70以上，军人，有导游证的免票！60岁以上半票！18:00后就没有公交车了！那里羊驼很漂亮，也很可爱 ！小孩可喜欢了！那里的海天一线，很漂亮！全部都是拍婚纱照的，你压根就不好意思靠近！适合情侣约会', '2016-03-16 12:33:31', '-2052989280:http://p0.meituan.net/shaitu/e0d00ea736cc7233f102d9bc15699e18569907.jpg;\n-2053153478:http://p1.meituan.net/shaitu/eaa355bb5d6ac95b3c672bf821e6d923908078.jpg;\n-2052883484:http://p1.meituan.net/shaitu/46175dd52db50b00d39ae05efbbb62c0555079.jpg;\n', 'None');
