INSERT INTO `meituan_daxiaodongtian` VALUES ('5244', 'lhb618317812', '287662796', '', '真是有点不好意思，去玩了这么久才来评价，第一次团购不怎么懂换票，工作人员挺热心的，在那里见到那么大的石头，景区景色很好，那天天气很好，好多游客都下水去玩了，水很清，景区卫生也挺好的，工作人员态度也好，我们一直是走路逛完的，和老公一起玩得挺开心的，值得一去的景区', '2016-02-24 20:55:18', '-2041919628:http://p0.meituan.net/shaitu/6d68893b1bb7471a634b7331f075f00675941.jpg;\n-2042094257:http://p0.meituan.net/shaitu/265cdfa2b7eb1797e31fe08afb42b7e5118564.jpg;\n-2042030245:http://p1.meituan.net/shaitu/f68d8526a296325cb67d0dd723ac9817118387.jpg;\n', 'None');
