INSERT INTO `meituan_daxiaodongtian` VALUES ('5253', 'tbP793574030', '64995345', '', '不错！出票速度挺快的，又经济实惠又方便，本以为一米四左右的孩子会按成人价买，所以就在美团上买了三张成人票，到了以后才问了只要是一米五左右的都按70算，就把多买的一张退了，原来想的退款肯定特麻烦，没想到特方便，退款很快就收到了！美团太好了！', '2016-02-23 21:12:55', '-2110805409:http://p1.meituan.net/shaitu/c30a664690f5ef77c41474aee73fd938259069.jpg;\n-2110798022:http://p0.meituan.net/shaitu/bf29c78e769496ee4ebc1f12b28c5565424120.jpg;\n-2110741023:http://p0.meituan.net/shaitu/c331f6455906c3b439dbf756d515f697240405.jpg;\n-2110763101:http://p1.meituan.net/shaitu/95939ced301ba468f8ed993425ee9aeb312454.jpg;\n-2110778298:http://p1.meituan.net/shaitu/776557c05687bab0aab5e64bdb7863b1273378.jpg;\n-2110769662:http://p1.meituan.net/shaitu/4718249c86f629ec53afc7c91a1028fe404735.jpg;\n-2110758725:http://p0.meituan.net/shaitu/0ab3b6f2cb423834029f2431adcc50ac291725.jpg;\n-2110758693:http://p1.meituan.net/shaitu/4200773890f432ea42520c0025871975270396.jpg;\n', 'None');
