INSERT INTO `meituan_daxiaodongtian` VALUES ('5326', '随遇而安hj1981', '276400371', '', '强烈推荐，全五分！首先出票速度非常快，赞一个！定好后一小时直接去就OK！相当方便！其次，是5A景区，环境超好，山不高很适合孩子和老人，海边有礁石，沙子里也有些细小的石子，和亚龙湾三亚湾的感觉是迥然不同，别有洞天！个人感觉不用做电瓶车，不是很远！门口有免费的讲解机租，需要押金100元，很方便！景色很不错，推荐！', '2016-02-17 17:20:53', '-2085799925:http://p1.meituan.net/shaitu/9ba9f8a7e4f4b3f775ea0b811ece4201101778.jpg;\n-2085720423:http://p1.meituan.net/shaitu/22af6b2ffb9fc06c409756c2b4af3dc672055.jpg;\n-2085765453:http://p0.meituan.net/shaitu/2ac2715d408e863be8c50e2611bcd40a67365.jpg;\n-2085705868:http://p1.meituan.net/shaitu/1abb090adbf7b87f1e97c958a31db31c101246.jpg;\n', 'None');
