INSERT INTO `meituan_daxiaodongtian` VALUES ('5344', '冷面杀手394', '70618859', '', '环境很好，来三亚旅游如果不想走太多的路，大小洞天是不错的选择。有电瓶车15元每人，我个人认为走路进去也没有多少路还能一路欣赏风景拍下照片。就是里面的小吃不是一点点的贵，最好自带。', '2016-02-15 20:37:33', '-2114477007:http://p0.meituan.net/shaitu/b0fc2ed87348d08b30c587296d8fe091234520.jpg;\n-2114516798:http://p0.meituan.net/shaitu/33b1c635c8f9a66ebb24bd41e6e67043234289.jpg;\n-2114526368:http://p1.meituan.net/shaitu/b7d1949b790f50da7f88388634764daa357449.jpg;\n-2114492116:http://p1.meituan.net/shaitu/73a7000d7f7ed206dec89c8f10078fb0453193.jpg;\n-2114465083:http://p0.meituan.net/shaitu/818266f5b76a8c2efdbdf6ccf27b324f364828.jpg;\n', 'None');
