INSERT INTO `meituan_daxiaodongtian` VALUES ('5354', 'bingdou5933', '9577191', 'https://img.meituan.net/avatar/f73f17205dd53562ff35d0e5e450dc1a124710.jpg', '景色挺美的，不同于之前去的蜈支洲岛，分界洲岛，三亚湾等沙滩的沙子那么细，这边的礁石居多，但是显得海水格外的清澈，整个景区被称为大洞天，沿海的景区，景色特别美，这边的游览车挺便宜的，才15一个人，比蜈支洲岛的150一个人便宜多了.', '2016-02-14 12:35:10', '-1960011420:http://p0.meituan.net/shaitu/3d3578e32a8447c9953e3f0ffafa08c4519925.jpg;\n-1960231845:http://p0.meituan.net/shaitu/0c5db51f3856a8334282c34b21fafc88506091.jpg;\n-1960216179:http://p0.meituan.net/shaitu/5571641a967fd2cf9effca327f3798e9479608.jpg;\n', 'None');
