INSERT INTO `meituan_daxiaodongtian` VALUES ('5356', 'oozhangoo', '7202399', 'https://img.meituan.net/avatar/399f8fc0bda78b73cc82a69d8856631c17457.jpg', '为什么什么门票都说要预约？去的时候就是给开了个票。拿票去换门票。各种扯淡～最主要的是景点真的是太没意思了 还5A。扯淡！门口检票人虎个脸，好像谁欠他钱一样！最***的是电瓶车！！！等了快一个小时才坐上！！！有这个时间，景点都逛完了！！！实在是三亚最坑的景点之一了', '2016-02-14 09:45:46', '', 'None');
