INSERT INTO `meituan_daxiaodongtian` VALUES ('5530', 'Zvc594515672', '70506729', '', '取票很方便，景区内非常舒服，虽然天气比较炎热，但景区内有很多绿荫，让人心情舒畅。随处可见供休息的长登，坐在清凉的绿荫下。脸上拂过阵阵海风，看孩子在海边找贝壳，追浪花，不知不觉一天就这样过去了，绝对是休闲的好去处。不过，景区内的东西比较贵，沙比较粗，有点硌脚。', '2016-01-21 13:21:37', '-2091359263:http://p1.meituan.net/shaitu/d6eb5ca081056871b18db4c095ff59ce269186.jpg;\n-2091386858:http://p0.meituan.net/shaitu/0f74bdc1ae3d884ec8b074ede99f6927125509.jpg;\n-2091359247:http://p0.meituan.net/shaitu/feb9396f07eec666ccc01a9fe1940334311325.jpg;\n', 'None');
