INSERT INTO `meituan_daxiaodongtian` VALUES ('5564', '李梅gogogo', '165359431', '', '环境很干净，旅游团很多人，地方很宽，旅游休闲好去处。我们是自驾游因时间没安排，没有逛完就出来了，下次去要在那待上一天。要给好评。我是团购票，购票好工作人员说要等一小时才能取票。三亚当天26度，很热的，三亚又是避冬的好去处，欢迎外地来海南过冬的幸福人们。', '2016-01-10 12:34:03', '-2096452711:http://p1.meituan.net/shaitu/c8c6b957f5887d1f32cb8a2f06223dbf44137.jpg;\n-2096452850:http://p1.meituan.net/shaitu/b769e9fa3ff7a4b75944e5ec6fdfdc1861879.jpg;\n-2096466719:http://p0.meituan.net/shaitu/c5ab581787917f346b908be73cdd548472312.jpg;\n', 'None');
