INSERT INTO `meituan_daxiaodongtian` VALUES ('5600', 'a别扒愣我', '75574741', 'https://img.meituan.net/avatar/0a5a9feb725ae9a3cc60660740bef463112486.jpg', '很不错，进场就给人好大的惊喜，工作人员都很热情 一个回头，就会被如画的风景吸引，有山、有海、有美丽的风景，值得一看', '2016-01-03 09:38:09', '-2084717776:http://p0.meituan.net/shaitu/e5661e7d604f244ac13c5ed1aedf9dd8257683.jpg;\n-2084630652:http://p1.meituan.net/shaitu/984bc1ffe944a5bd55739b43c5cda3aa256856.jpg;\n-2084673769:http://p0.meituan.net/shaitu/010d1d83f1283422c6a8a2c2959a6a19390006.jpg;\n', 'None');
