INSERT INTO `meituan_dongshanling` VALUES ('695', 'XbX701011751', '61481101', '', '景色很美，值得一看，山门到潮音寺中间的石刻群比较漂亮。人文和自然风光相结合。景区不是很大。如果时间允许的话可以走上山走下山，时间紧的话建议，坐索道上山冲上，有老年人和小朋友的情况下可以坐游览车上山。景区内摩崖石刻很多，多是民国时期的题词。最高处是缆车站，真武大殿、潮音寺和国内的其他景区差不多，好像是被承包了。', '2016-03-01 07:09:36', '-1940709089:http://p1.meituan.net/shaitu/8e19ceda6d9df8bdfabbbbdceb1d40a5633724.jpg;\n-1940709073:http://p1.meituan.net/shaitu/8f8d4890ce1541c4e259b55b3362a505478118.jpg;\n-1940496632:http://p1.meituan.net/shaitu/ddcbeff94d18fd86d1e9abb4d27f118f580380.jpg;\n-1941099530:http://p0.meituan.net/shaitu/5a1f264a99db9805f6edad6827755b0d517106.jpg;\n-1941040834:http://p0.meituan.net/shaitu/efd4b7811044fe476b4a1ef44da5d617578951.jpg;\n-1940525476:http://p1.meituan.net/shaitu/7d3dd32613d6dbef62639bc67516064b552605.jpg;\n-1940593792:http://p1.meituan.net/shaitu/716d042a593d8a622217eb217e0650cd510748.jpg;\n-1940470860:http://p1.meituan.net/shaitu/6af324ec1bd3ab3a261b7768615c6b73564352.jpg;\n', 'None');
