INSERT INTO `meituan_dongwuyuan` VALUES ('79', '宝呗卟乖', '8629304', 'https://img.meituan.net/avatar/94e313ca916677c09bcd2fb1fb200c1e24220.jpg', '里面的逛了三四个小时，喂动物十元一份！可以近距离接触猴子，还会在行人中穿梭！带小孩子去看看很好！熊猫馆还在建设中，没有熊猫！有简单的恐龙，可以动，可以发出声音，喜欢恐龙的可以看看，不喜欢的会觉得害怕吧！其他的跟动物园没有什么区别！', '2018-10-06 16:43:48', '', 'None');
