INSERT INTO `meituan_dongwuyuan` VALUES ('288', '雪影子好', '56684394', '', '海口野生动物园比较?，可以开车入园游览（只要3元停车费[偷笑]）；入园时会每人发一个电子解说器，随走随听很方便；在车里看旁边的猛兽，有些小激动[呲牙]；还可以买食物喂动物，可以喂鳄鱼哦！另外还有一条侏罗纪大道，总之小朋友觉得棒棒哒！', '2018-08-20 11:29:28', '', 'None');
