INSERT INTO `meituan_dongwuyuan` VALUES ('1002', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '园区很大，可以坐园区观光车/自驾车游览一部分，剩下的是步行游览区。游览时间大概2-3小时。植物种类繁多，建议可以多留心一下，\n动植物门票相比之下，性价比并不高。不喜不厌不推荐。\n很多动物区投食是，另外付的。\n园区内的瓶装水较贵。', '2018-02-20 09:03:27', '', 'None');
