INSERT INTO `meituan_dongwuyuan` VALUES ('1020', 'JK冰冰', '192007305', '', '一点都不好  对不起这个价格  竟然还有驴在里边  而且不开车进去的游客  做里边的缆车  到了动物那里都不停车  直接开过去一点都不人性化  所以还是自驾游比较好  不过也没有什么动物  不值得一看  还不如火山口呢  不过门口那个弄电子导游的工作人员挺好  指给我们路', '2018-02-18 22:38:54', '', 'None');
