INSERT INTO `meituan_dongwuyuan` VALUES ('4034', '小鸡69', '10811638', '', '初二去的，人较多，团购换票速度慢，园区景色很美，猛兽区老虎是管关起来的，比较好的是看到四只狮虎兽，孩子最喜欢的还是鹿和大象，给鹿和大象喂食，玩的很开心，动物种类不算特别多，但是热带植物很有特色，值得一去。', '2017-01-30 11:18:37', '', 'None');
