INSERT INTO `meituan_dongwuyuan` VALUES ('4989', '爱吃鸡腿的木木', '624552746', '', '车游挺轻松的，国庆人多最好预留多点时间，最重要的，一定一定车上自备干粮！有人低血糖都晕倒了，因为车很多都是蜗速前进，整个景区只有一个餐厅在车游区的终点站，我们今天三个小时才到餐厅，路上肯定饿?步行区下雨没去看，等以后再去咯', '2016-10-04 21:29:39', '', 'None');
