INSERT INTO `meituan_dongwuyuan` VALUES ('5498', 'Igo935945094', '94176786', '', '带儿子去的，那天入园有点晚了，孩子没有玩尽兴，没有看到他想看到的黄猩猩。猛兽区与老虎、狮子、黑熊近距离接触又害怕又刺激，去猴园的时候，刚进去那些猴子围过来就叫，孩子有点怕，不敢向前走，后面工作人员来了，其实不惹它，不当着它面吃东西还是很安全的。', '2016-07-30 16:28:35', '-2111705872:http://p1.meituan.net/shaitu/2f4b44179f4c2b70ef22a17fa4d963a0104195.jpg;\n-2111683525:http://p1.meituan.net/shaitu/6c88679e5696f0e86b61c21250a3e593251612.jpg;\n-2111698526:http://p0.meituan.net/shaitu/80391629a96ce069bd28b8e0acd046a7153562.jpg;\n-2111684818:http://p1.meituan.net/shaitu/1aa87a387775458b567d2c0cfbf060e5126739.jpg;\n-2111649467:http://p1.meituan.net/shaitu/d5139b8e59d6ac4b8a33cbf0f002c6ab176595.jpg;\n', 'None');
