INSERT INTO `meituan_dongwuyuan` VALUES ('5711', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '带小朋友去的，小朋友很兴奋，特别喜欢大象，矮马，还有孔雀！买了10块钱一小蓝的香蕉喂大象，那个大姐非常热心，给我们讲解，小朋友问题多，耐心大姐一一介绍，小朋友说想看梅花鹿，大姐喊“鹿鹿”，一群梅花鹿就朝我们跑来，很幸运的看到一只长着成熟角的公鹿！小孩超兴奋！还看到大象用鼻子哼气赶小鹿，哈哈～特别可爱！不虚此行啊！\n', '2016-07-08 07:05:49', '-2142924298:http://p1.meituan.net/shaitu/37fc5e7f29fe9fbe7c8ebe060d99aa9e384573.jpg;\n-2142924297:http://p1.meituan.net/shaitu/164265d3ef6db5126def3d2cfd7a52f4370382.jpg;\n-2142924296:http://p0.meituan.net/shaitu/95cdf2cf196db789eb2f92d8b6d540af377783.jpg;\n-2142924295:http://p0.meituan.net/shaitu/d4d332347f045f6190331b1dbcec9a08425684.jpg;\n-2142924294:http://p0.meituan.net/shaitu/a696eb665c2d520033663164cc64ea1a404032.jpg;\n-2142924293:http://p1.meituan.net/shaitu/6d36a8dc10e9072e4648852928492532535520.jpg;\n-2142924292:http://p0.meituan.net/shaitu/720f26b9d79206b10c2ecca32044ea0a441952.jpg;\n-2142924291:http://p1.meituan.net/shaitu/83cb2babdc005e26ed7d9276df69ab1d378745.jpg;\n', 'None');
