INSERT INTO `meituan_dongwuyuan` VALUES ('5974', '兰歆贝贝', '108125846', 'https://img.meituan.net/avatar/c7459e2a13001ff40e83dc8047ef2b6e11793.jpg', '节日去人多堵车太厉害了，建议想要去的亲还是选在平时去好点。离海口不远，自驾车去比较方便。看到评论上有狮子的，就是冲着狮子才带小孩去的，结果到那里连狮子的影子都没见着，有点小失望。不过里面动物还挺多的了。', '2016-06-02 08:05:10', '-1937764386:http://p0.meituan.net/shaitu/444c91226b8903369ec6a19b3269adab151982.jpg;\n-1937381487:http://p1.meituan.net/shaitu/82349dadaebeaee740c63af494510dce165450.jpg;\n-1937409217:http://p0.meituan.net/shaitu/8484ebb1f3dcaa70feaba03211f62dba95898.jpg;\n-1937292272:http://p1.meituan.net/shaitu/f56fc4674b4c00a66b5b95378f3f1674152675.jpg;\n-1937442647:http://p0.meituan.net/shaitu/d4a6a0f3f131b5168c6136f76b1edc0b116069.jpg;\n-1937206328:http://p1.meituan.net/shaitu/e7cd10dfad48e4460c123e62a1f6e7c0197307.jpg;\n-1937359273:http://p0.meituan.net/shaitu/62d26373d84a3e452d00e368313a55ef131995.jpg;\n', 'None');
