INSERT INTO `meituan_dongwuyuan` VALUES ('5980', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '挺不错的！很适合自驾亲子游，我们是一家人去的！老人和孩子都很开心，我们也感觉来对地方了！就是出票有点慢，非要等够两个小时，好在听取了前面团购的人的意见提前买了票再出发，到那里时间刚刚好！下次有机会还会再来的，离家也不是很远！', '2016-06-01 18:56:08', '-1937105750:http://p1.meituan.net/shaitu/89f39f2b434c144a181bf84cd7c8e411158997.jpg;\n-1936546531:http://p0.meituan.net/shaitu/214fdc8857360485e31bac2114304361128962.jpg;\n-1936964042:http://p0.meituan.net/shaitu/c4ff758d10f07668341a7d603d68f460143931.jpg;\n', 'None');
