INSERT INTO `meituan_dongwuyuan` VALUES ('6049', '金叹偶吧么么哒', '70560620', 'https://img.meituan.net/avatar/8a79416dc1cad7806d5fe373defc1e20251564.jpg', '交通很方便~直接坐中巴车就行了，速度也很快~比想象的好太多了！原本之前看美团评论，其他人的评价…看了都有点不想去了…因为是带外婆来旅游的~我们两人玩的很开心，本来外婆都可以免费进去看的，只是没带身份证所以不能证明70以上…说拍传到手机上可以么？也说不行…而我是学生，本来也可以享受学生价的，也没带过来…   里面还是有不少动物可以看的~管理员也很好~态度友善~   去的时候河马都睡到水里了怎么都不出来~只好买吃的逗他出来了~很累，很晒！但是我们很开心！猴子也很温顺，我们还跟管理员买了地瓜喂他们~可逗了~有老虎，狮子，黑熊，鸵鸟，骆驼，大象等等', '2016-05-20 16:34:04', '-1931191337:http://p1.meituan.net/shaitu/dcd8ec222d5fdbd3f5324188231c5259886250.jpg;\n-1931191353:http://p1.meituan.net/shaitu/c78f2e90eef38f9b65ca30ff2270c77f1567885.jpg;\n-1931806846:http://p1.meituan.net/shaitu/4cb3615c573ca12214e7a53352c5e3be1299032.jpg;\n-1931191369:http://p0.meituan.net/shaitu/e7975447f3c7e4d7b5d51e6525361fbc707039.jpg;\n-1931006056:http://p1.meituan.net/shaitu/981d086ce7f9a9641fbe8dc33ca8d9301115158.jpg;\n-1931106992:http://p1.meituan.net/shaitu/7de08f5df50e02d2045897e11f5b9e061436401.jpg;\n-1931107040:http://p1.meituan.net/shaitu/abde1434884f664390022950e48dda921444408.jpg;\n-1931107024:http://p0.meituan.net/shaitu/d0f72894f6b38a92b720eab2348cc45a1143194.jpg;\n', 'None');
