INSERT INTO `meituan_dongwuyuan` VALUES ('6117', '羅小蛋', '44481594', 'https://img.meituan.net/avatar/f108cfce6025cbcf59d8f200e356914517924.jpg', '服务区的工作人员工作效率也很快，带父母去的，自驾游比较好些，可以多转几圈看看狮子老虎狗熊……估计是天气太热！动物都懒羊羊的，没精神！河马也是泡在水里，动都不动！倒是挺多鳄鱼的，海南现在目前也就这个动物园可以看看咯！', '2016-05-10 20:29:57', '-2098568158:http://p0.meituan.net/shaitu/9fd4bcef1457705c29968b2da0fa74aa170961.jpg;\n-2098568142:http://p1.meituan.net/shaitu/2f9a49da6ad02834aedd7978ecd83850159848.jpg;\n-2098591269:http://p1.meituan.net/shaitu/65627898c520b98db7c50b36393e40b2166138.jpg;\n', 'None');
