INSERT INTO `meituan_dongwuyuan` VALUES ('6197', '爱之百分百', '13989087', '', '五一节日去的，人太多。从到门口附近就开始塞车，人太多。居然在车待了两个来小时才逛完车览区。动物挺多，人多车多。到哪都排队。景色挺美，心情不美烈日当空到步行景区人已经累得不行是一个值得去的地方。很多动物只在图书上见过没这么近距离接触过，是一次非常棒的体验。团票不给电子讲解器。如果想要讲解的朋友就团购剩一张。然后实地购一张就有一个。几个人轮流听。这次去我也是这样做。只是我听了一站就放哪里不用了。最好别节日去，那是我们排队给动物看不是去看动物。希望我的经历对你有帮助。呵呵！', '2016-05-01 23:48:16', '-2045534107:http://p0.meituan.net/shaitu/be424cb524dbc89865f071a369aabee078008.jpg;\n-2045646177:http://p0.meituan.net/shaitu/2d34d24f226347f87a1ec9186861003875972.jpg;\n-2045731694:http://p1.meituan.net/shaitu/01c03fbff97cadecbe2b5941bc278c1783947.jpg;\n-2045579040:http://p1.meituan.net/shaitu/4508c031e028d65f36bfa0d72ab808d5108570.jpg;\n-2045618239:http://p1.meituan.net/shaitu/04f3760b11e7d3ff61ac701a0f10733e114526.jpg;\n-2045480124:http://p1.meituan.net/shaitu/17e2e0857eb357a718fa8775967e4618138362.jpg;\n-2045487780:http://p1.meituan.net/shaitu/949a24cca27f680f5a25c5b9f869a19798491.jpg;\n', 'None');
