INSERT INTO `meituan_dongwuyuan` VALUES ('6757', 'LaK912998872', '116157544', '', '春节初四去，门口堵的一塌糊涂，圆内小路也堵。管理不到位，园内没有几个野生动物，少的可怜几头狮子在笼内，只有几头熊还被控制在房子周围。一头大象的伴侣是几头小鹿。团购去的，换票等半小时。干脆去掉猛兽区还好，明不符实！', '2016-02-20 12:06:06', '-2107227398:http://p1.meituan.net/shaitu/0321275509400cd855aea5f57ab7e1ac410943.jpg;\n-2107221184:http://p1.meituan.net/shaitu/f4ed438bec120c10a0c4995857a41654512990.jpg;\n-2107244033:http://p0.meituan.net/shaitu/8b9521eba4539788d49c4920dccd107f526974.jpg;\n-2107178456:http://p0.meituan.net/shaitu/d55db616aa63118f3f5aa6169a660108711552.jpg;\n-2107206602:http://p1.meituan.net/shaitu/faaf939d5ad52483db19c419ad887e95290946.jpg;\n-2107161895:http://p1.meituan.net/shaitu/4b9253ad8e15a3ad95d41c8116dd1aa4259330.jpg;\n-2107190942:http://p1.meituan.net/shaitu/9a9dc893175f41f0d4517848ad1cbfe0437703.jpg;\n', 'None');
