INSERT INTO `meituan_dongwuyuan` VALUES ('6816', 'chen93520', '58006433', '', '一家子的一日游！真的很不错！小朋友看着开心！动物有好多种！都给了不一样的惊喜！这样的旅游 跟宝宝互动一下真的很好！看着宝宝蹦蹦跳跳那开心的样子好满足！好聪明！拿着买来的零食就自己抢着吃了!扔给它还会接着吃!好可爱！最可惜的是没有长颈鹿！', '2016-02-16 10:27:17', '-2054921198:http://p0.meituan.net/shaitu/8b64e33f0b101e3594916c06d421a4b0545607.jpg;\n-2054764984:http://p0.meituan.net/shaitu/d97e3874f889f5c18395f4184ac7cd0d891115.jpg;\n-2054805309:http://p1.meituan.net/shaitu/fca729d5c8182fbf53916bb2f64a2cf91199214.jpg;\n-2054803557:http://p1.meituan.net/shaitu/1254c6103ac4cb77a9e5cd38bd49143d887720.jpg;\n-2054736260:http://p0.meituan.net/shaitu/d0de9c35784c9b8325afd428a56afe411408970.jpg;\n-2054712556:http://p0.meituan.net/shaitu/b75a2aca53be069f98ed2b2f4f3fba98743415.jpg;\n-2054752795:http://p0.meituan.net/shaitu/93fb38a8923eeb839bf4db57ec089828943606.jpg;\n-2054855569:http://p0.meituan.net/shaitu/e8ca375e179bffac77f00c36fc6932fe806658.jpg;\n', 'None');
