INSERT INTO `meituan_dongwuyuan` VALUES ('7016', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '孩子开心极了，见到真实的大象，老虎，狮子。老虎的叫声太可怕了，就在耳边，震天吼啊。大象看着凶狠，其实很温柔的，用鼻子卷走地瓜片，迅速放进嘴里，很可爱的。黑熊就从车身走过，很清楚看见它们抢食吃。还有好些动物，值得一游。', '2016-01-31 18:53:52', '-1937393234:http://p0.meituan.net/shaitu/19e5edcb56b0792f1e87826a4a252aa9778077.jpg;\n-1937036467:http://p1.meituan.net/shaitu/d991e756c8ea71e81e78623fb762247c619432.jpg;\n-1936993193:http://p1.meituan.net/shaitu/9a45a3f9984093dbecffd2a053bdfc0c483168.jpg;\n-1937393250:http://p0.meituan.net/shaitu/f028ffe0b75b1716e4f63e157aa424d3550567.jpg;\n-1937393266:http://p0.meituan.net/shaitu/a9540e3a3628a50abe8db307f9aa2f2c883410.jpg;\n-1937041325:http://p0.meituan.net/shaitu/e699952511e3306fa8153d8bcef1111a513508.jpg;\n-1936837368:http://p0.meituan.net/shaitu/c99f61cc7faa446a145cece886c719c0484846.jpg;\n-1936801820:http://p1.meituan.net/shaitu/ad7edbd4bfd97033522bac73d664fb86811859.jpg;\n', 'None');
