INSERT INTO `meituan_dongwuyuan` VALUES ('7225', '百灵妹', '204757901', 'https://img.meituan.net/avatar/38f78a9d8bf6b7a2c276f31445eb18473797.jpg', '那天是冒着雨去的，赶在国庆节去了！那天是10月6日去的！在海南比较起来！这里还是不错的！动物种类挺多！孩子很兴奋！特别是看见大象那会儿可乐了！还给大象喂食了！可是，大象有点不讲卫生！多久不给大象洗澡了？这是我儿子问我的！我都无语了！那个猴山就不错！值得推荐！', '2016-01-01 10:58:01', '-2113061056:http://p0.meituan.net/shaitu/5af0e01dc48ad3756f377a44e230ff9816374.jpg;\n-2113004807:http://p1.meituan.net/shaitu/849d35d4056c6baa9f60547ad52132e813198.jpg;\n-2113050734:http://p0.meituan.net/shaitu/99ca968d4ca8aa8db4e97e214f569ddb17547.jpg;\n-2113039893:http://p1.meituan.net/shaitu/a42a29c5bada41d34edd2184a35dfa9014887.jpg;\n-2113023692:http://p0.meituan.net/shaitu/7c30d85aae1ca900dfc4dbbfd6ea0a6015902.jpg;\n-2113088481:http://p1.meituan.net/shaitu/6d3c3912712e479d3f9975ce80672c8c23356.jpg;\n-2113072678:http://p0.meituan.net/shaitu/830a7847d9461cfa68c8ab948c15e00426304.jpg;\n', 'None');
