INSERT INTO `meituan_dongwuyuan` VALUES ('7230', 'KdL435269845', '256433945', '', '来野生动物园玩几次了，设施这块有待提高，需要创新思维建出亮点，二十多年前去过国内一家动物园，拿老虎来说他们建一个屋子类的建筑又类似山洞规模二亩大左右，老虎圈养在铁栏里，那老虎的叫吼声响澈整个山洞，另人毛骨悚然…印象深刻。目前野生动物园内多年如一有些太陈旧，动物种类需要引进长劲鹿或班马一类的…', '2015-12-31 01:21:32', '-2127457801:http://p0.meituan.net/shaitu/94de313ee837a6d1cac7d7be5b0c469d219254.jpg;\n-2127444132:http://p0.meituan.net/shaitu/161b8c65ef5e308900a148f7cc0d63f5454701.jpg;\n-2127407491:http://p1.meituan.net/shaitu/84f4704502d85c640cdbc70f47464fd3342191.jpg;\n', 'None');
