INSERT INTO `meituan_dongwuyuan` VALUES ('7634', 'aDN503103595', '74023312', '', '环境挺好的，这个时候的人不多。动物们都悠闲的趴着，没啥精神，不知道是不是因为今天天气热。工作人员但是挺热情的。主要是带孩子去玩的后面孩子睡了，步行区没怎么看就回来了', '2015-10-22 15:54:26', '', 'None');
