INSERT INTO `meituan_dongwuyuan` VALUES ('8030', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '带孩子去看看动物，增加见识，不过动物没有以前多了，可能是要搬了吧', '2015-09-08 20:38:40', '', 'None');
