INSERT INTO `meituan_dongwuyuan` VALUES ('8319', '高王晓', '7172612', '', '园区占地面积大，植被区茂密，比较有看点的是猛兽区和猴岛和大象，猴岛的小猴子很有灵性，而且体型袖珍长相讨喜，动物都太瘦了，希望多改善伙食，让小动物吃饱点哦，步行区的小动都关在笼子里，异味很大，卫生状况堪忧', '2015-08-04 16:40:24', '', 'None');
