INSERT INTO `meituan_dongwuyuan` VALUES ('8413', '魄念', '46862128', '', '还是不错的。只是很多动物都是饿的。鸟类的毛都掉了好多', '2015-07-26 16:02:54', '', 'None');
INSERT INTO `meituan_dongwuyuan` VALUES ('8414', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '第一次来玩感觉真不错，带小孩就更好玩了，下次还会去。这次去的时候走的东线，那边路还没有修好，建议大家不要走，就走中线路好又快；去那边最好要开车进去，不然有些地方景点走路的话不让进去，因为狮子熊等就在路上走', '2015-07-26 15:15:17', '-2043732523:http://p1.meituan.net/shaitu/6af29f9935c54af04520f4be7c998f1772026.jpg;\n-2043764832:http://p1.meituan.net/shaitu/d4e7f304a1af7d7a579710ac754d238f67430.jpg;\n-2043782165:http://p0.meituan.net/shaitu/a5bf131b7ad015d8dc12a343a45ed58e118769.jpg;\n', 'None');
