INSERT INTO `meituan_dongwuyuan` VALUES ('8539', '牧歌0102', '5862595', '', '真的是太精彩了，可以开车进去游览，隔着车窗玻璃可以看到老虎，老虎真的是野生放养的哦，还有虎狮兽，大象可以直接摸到鼻子，可以喂食，还有鳄鱼可以喂鱼吃，虽然凶猛，但是也有自己别具一格的特色，车游后还有步行去。', '2015-07-06 09:26:51', '-2011081870:http://p1.meituan.net/shaitu/e91745fd5c69c83d9653f9f447236029359235.jpg;\n-2010763536:http://p1.meituan.net/shaitu/a0bba22208bbaae0647ee25e29733930465237.jpg;\n-2010802135:http://p0.meituan.net/shaitu/e169f62e13a0979d5aac0ca9ebd2a6bd423567.jpg;\n-2010738923:http://p0.meituan.net/shaitu/813ab7e8fd8c9522b055438f64be04fc566546.jpg;\n', 'None');
