INSERT INTO `meituan_dongwuyuan` VALUES ('8901', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '本来以为开车从海屯高速路下去近一点，没想到下高速后的路太烂了。到动物园里又堵车，就是海南热带动植物园的名声好听而已，其实和金牛岭公园的动物园差不多！', '2015-05-01 21:43:12', '', 'None');
