INSERT INTO `meituan_dongwuyuan` VALUES ('8922', '美鑫儿', '5953690', 'https://img.meituan.net/avatar/48e2827357d49de4e92bc7d970e2503e23555.jpg', '天气不热，去玩很舒服，猴子那里宝宝最喜欢，团购的没有给讲解器，地方不大但是感觉安排很合理，就是到处都在卖东西喂动物有点厌烦了，总的来说还是挺不错带孩子去看看是个好地方', '2015-04-25 16:36:13', '-1996952363:http://p1.meituan.net/shaitu/2a09215ec97bb96f1e13b833e566229e447654.jpg;\n', 'None');
