INSERT INTO `meituan_dongwuyuan` VALUES ('8940', 'LLyzx1224', '60363600', 'https://img.meituan.net/avatar/751ab8f4ee17e9a426a3ee8003cc65e3188008.jpg', '感觉还挺好的，动物园很大，里面动物也蛮多的，儿子看见大象和老虎就不肯走了，就是不让喂自带的食物，非得让买他们那里的红薯喂动物。就是去的那天太热了，太晒了，有点热的受不了。猴子园区也还挺大的，可以进去与猴子近距离接触', '2015-04-16 14:11:11', '-2075142237:http://p1.meituan.net/shaitu/0acf2c99f406e19b4dec318203a10484298692.jpg;\n-2075085900:http://p1.meituan.net/shaitu/f84ee83be4787e19b762de8ae0c3a1c1310926.jpg;\n-2075091666:http://p0.meituan.net/shaitu/7bfad6d10e0947a8d88f3075c809be11497411.jpg;\n', 'None');
