INSERT INTO `meituan_dongwuyuan` VALUES ('8951', 'evil桔梗', '17930778', 'https://img.meituan.net/avatar/a2084e75a723101e24d3da470a73ea7914128.jpg', '动物太少了，分步行区和车行区。车行区有狮子，黑熊老虎。不行那边就鳄鱼，鸟这些。我是听朋友说这边比金牛岭多才来的，来了才发现其实都差不多。金牛岭才25块钱。从海口过去有点远。主要是没什么好看的。有点失望。', '2015-04-12 11:19:20', '-2084566528:http://p0.meituan.net/shaitu/88a5f07e2840488cea372c3a751e8e71104157.jpg;\n-2084494951:http://p1.meituan.net/shaitu/efc8d8f81b9fa6604b94cf279d77e238106251.jpg;\n-2084493538:http://p0.meituan.net/shaitu/46389a95a9203a7608d170dc250f7f6f81923.jpg;\n-2084536282:http://p1.meituan.net/shaitu/3a8384a93c70d778955ed9ac7cb4cc0c109111.jpg;\n-2084493506:http://p0.meituan.net/shaitu/5d858004a961d615e0ad916cad8cf4b290612.jpg;\n-2084462475:http://p0.meituan.net/shaitu/f44750d2ff03a7a897f6136fe656705b40148.jpg;\n', 'None');
