INSERT INTO `meituan_dongwuyuan` VALUES ('9013', '匿名用户', '0', 'https://www.dpfile.com/ugc/user/anonymous.png', '海南野生动植物园，距离市区30公里，与各种动物绝对零距离。行为乖张的火烈鸟，凶相毕露的鳄鱼，温顺慈祥的亚洲象，孤芳自赏的孔雀，无忧无虑的黑熊什么的自不必说。瞧瞧这位狮虎兽先生，威猛强壮的外表也掩饰不了一颗卑微的“***”心。[调皮]再看看鸵鸟小姐吧，黑色超短裙下修长的双腿交叉，下颌微微扬起，毛茸茸的眼睛都不正眼瞧人，真真是仪态万千啊！亚洲第一猴山可不是浪得虚名额，占地面积之大让人惊叹，除了猴山，水帘洞，还有湖景房[色]不过这些小家伙可比峨眉山那帮没教养的家伙好多了，看看多么和谐自然[呲牙][呲牙]', '2015-03-22 18:09:41', '-2108069781:http://p1.meituan.net/shaitu/5ff9889ae0a6c66ef64dbda74342cbc059916.jpg;\n-2108047211:http://p1.meituan.net/shaitu/62de0c9c6a8b55ec3e013fbcb35a707c71483.jpg;\n-2108091758:http://p1.meituan.net/shaitu/1c364249d89cd352eca060610d29be2b53753.jpg;\n', 'None');
