INSERT INTO `meituan_dongwuyuan` VALUES ('9064', 'nonjob', '6096937', '', '比火山口差一点！主要想看的鸵鸟、羊驼没有！到动植物园的路有一段太难走！另外，海口本地户口竟然不半价，网上查到的人家售票窗口不认，哎！票价偏贵！但卖家的票还比较便宜，卖家服务不错！赞一个', '2015-03-12 18:05:25', '', 'None');
